(function(){let selectedInputElement=null;document.addEventListener("click",(event=>{const target=event.target;if((target.tagName==="INPUT"||target.tagName==="TEXTAREA")&&!target.disabled&&!target.readOnly&&target.isConnected){selectedInputElement=target}else{selectedInputElement=null}}));chrome.runtime.onMessage.addListener(((message,sender,sendResponse)=>{if(message.action==="setInputValue"&&selectedInputElement&&typeof message.data==="string"){selectedInputElement.value=message.data.slice(0,1e3);const inputEvent=new Event("input",{bubbles:true});const changeEvent=new Event("change",{bubbles:true});selectedInputElement.dispatchEvent(inputEvent);selectedInputElement.dispatchEvent(changeEvent)}sendResponse({success:!!selectedInputElement})}))})();